mapper.select:
SELECT ou.orderNo AS isUp, olb.* FROM ( select id, createUid, createTime, updateUid, updateTime, cityCode, productCode, orderNo, customerName, borrowingAmount, borrowingDay, cooperativeAgencyId, cooperativeAgencyName, channelManagerUid, channelManagerName, previousHandler, previousHandlerUid, previousHandleTime, currentHandlerUid, currentHandler, state, processId, source, acceptMemberName, appShowValue1, appShowValue2, NULL AS appShowValue3, NULL AS estateType, NULL AS estateTypeCode, NULL AS estateNo, NULL AS totalPrice, NULL AS netPrice, NULL AS bankId, NULL AS bankName, NULL AS subBankId, NULL AS subBankName, NULL AS custManagerName, NULL AS custManagerNameCode, NULL AS operate, agencyId, cityName, branchCompany, productName, contractNo, acceptMemberUid, planPaymentTime, lendingTime, notarialUid, facesignUid, relationOrderNo, foreclosureTimeStr, isFace, isRelationOrder from `anjbo-server-order`.`tbl_order_list_base` where
1 = 1
(currentHandlerUid in (${currentHandlerUid}) or previousHandlerUid = #{updateUid} or notarialUid in (${currentHandlerUid}) or facesignUid in (${currentHandlerUid})
or acceptMemberUid in (${currentHandlerUid})
or channelManagerUid in (${currentHandlerUid})
or createUid in (${updateUid})
or orderNo in (${orderNo})
)
1=1
and agencyId = #{agencyId}
orderNo in (${orderNo})
and agencyId = #{agencyId}
(currentHandlerUid = #{currentHandlerUid} or notarialUid = #{currentHandlerUid} or facesignUid = #{currentHandlerUid})
and agencyId = #{agencyId}
(currentHandlerUid in (${currentHandlerUid}) or previousHandlerUid = #{updateUid} or notarialUid in (${currentHandlerUid}) or facesignUid in (${currentHandlerUid})
or acceptMemberUid in (${currentHandlerUid})
or channelManagerUid in (${currentHandlerUid})
or createUid in (${updateUid})
or orderNo in (${orderNo})
or agencyId = #{agencyId}
or cooperativeAgencyId = #{cooperativeAgencyId}
)
1=1
and customerName like CONCAT('%',#{customerName},'%')
