and branchCompany = #{branchCompany}
and (channelManagerName like CONCAT('%',#{searchName},'%') or customerName like CONCAT('%',#{searchName},'%') or acceptMemberName like CONCAT('%',#{searchName},'%'))
/*and state != '已关闭' */ UNION ALL ( SELECT id, createUid, createTime, updateUid, updateTime, cityCode, productCode, orderNo, NAME AS customerName, borrowingAmount, loanLimit, cooperativeAgencyId, cooperativeAgencyName, channelManagerUid, channelManagerName, previousHandler, previousHandlerUid, previousHandleTime, currentHandlerUid, currentHandler, state, processId, source, createPeopleName, appShowValue1, appShowValue2, appShowValue3, estateType, estateTypeCode, estateNo, totalPrice, netPrice, bankId, bankName, subBankId, subBankName, custManagerName, custManagerNameCode, operate, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL FROM `anjbo-server-product-data`.`tbl_cm_list` where
1 = 1
(currentHandlerUid in (${currentHandlerUid}) or previousHandlerUid = #{updateUid}
or channelManagerUid in (${currentHandlerUid})
or createUid in (${updateUid})
or orderNo in (${orderNo})
)
1=1
orderNo in (${orderNo})
(currentHandlerUid = #{currentHandlerUid})
(currentHandlerUid in (${currentHandlerUid}) or previousHandlerUid = #{updateUid}
or channelManagerUid in (${currentHandlerUid})
or createUid in (${updateUid})
or orderNo in (${orderNo})
