mapper.select:
SELECT cityCode,cityName, productCode,productName, sum(loanAmount) AS loanAmount FROM tbl_finance_paymentreport WHERE status IN (0,2)
AND DATE_ADD(CURDATE(),INTERVAL 1 DAY) = DATE(estimateInLoanTime)
AND DATE_FORMAT(estimateInLoanTime,'%Y-%m-%d') BETWEEN DATE_FORMAT(NOW(),'%Y-%m-%d') AND DATE_ADD(CURDATE(),INTERVAL #{day} DAY)
AND cityCode = #{cityCode}
AND productCode = #{productCode}
GROUP BY cityCode

SELECT cityCode,cityName, productCode,productName, sum(loanAmount) AS loanAmount FROM tbl_finance_report WHERE status IN (0,2)
AND DATE_ADD(CURDATE(),INTERVAL 1 DAY) = DATE(estimateOutLoanTime)
AND DATE_FORMAT(estimateOutLoanTime,'%Y-%m-%d') BETWEEN DATE_FORMAT(NOW(),'%Y-%m-%d') AND DATE_ADD(CURDATE(),INTERVAL #{day} DAY)
AND cityCode = #{cityCode}
AND productCode = #{productCode}
GROUP BY cityCode

SELECT count(1) AS `count`, processId FROM tbl_order_list_base WHERE (processId='placeOrder' OR processId='auditFirst' OR processId='auditFinal' OR processId='auditOfficer') AND state!='已关闭' AND productCode!='04'
AND cityCode = #{cityCode}
AND productCode = #{productCode}
GROUP BY processId

SELECT u.name,u.deptId,u.deptIdArray,u.cityCode,u.agencyId,u.uid FROM tbl_user_base u LEFT JOIN tbl_user_role r ON r.id = u.roleId WHERE r.agencyId = #{agencyId} AND r.`name` = #{roleName} AND isEnable=0
AND (u.deptId=#{deptId} OR u.deptIdArray LIKE CONCAT('%',#{deptId},'%'))
AND u.cityCode = #{cityCode}

SELECT f.cityCode,f.cityName name FROM tbl_order_report_income_file f WHERE 1=1
AND f.cityCode=#{cityCode}
AND f.cityCode IS NOT NULL AND f.effective=1
AND (ISNULL(f.cityCode) OR f.effective=2)
GROUP BY f.cityCode LIMIT #{start},#{pageSize}

SELECT d.`name`,d.`code` FROM tbl_common_dict d WHERE d.type=#{type} AND (d.pcode ='' OR ISNULL(d.pcode))
AND d.`code`=#{cityCode}
GROUP BY d.`code`
LIMIT #{start},#{pageSize}

SELECT
	d1.id,d1.name,d1.agencyId,d2.id AS pid ,d2.name AS pname
FROM  tbl_user_dept d1,tbl_user_dept d2
WHERE d1.agencyId=#{agencyId}
AND d1.pid = d2.id

	AND d1.id = #{deptId}

SELECT
*
FROM tbl_order_report_income_file
WHERE 1=1

	AND effective = #{effective}


	AND deptId = #{deptId}


	AND cityCode = #{cityCode}

SELECT a.`name` AS agencyName ,a.surplusQuota ,a.creditLimit ,a.cooperativeModeId ,a.id AS agencyId FROM tbl_customer_agency a
WHERE a.signStatus = 2 AND a.STATUS = 1
AND a.cooperativeModeId=#{cooperativeModeId}
ORDER BY 1=1
,surplusQuota ${surplusQuotaSort}
,creditLimit ${creditLimitSort}

SELECT b.cooperativeAgencyId AS agencyId ,SUM(l.lendingAmount) AS loanAmount ,COUNT(DISTINCT l.orderNo) AS orderCount ,SUM(l.income) AS income FROM tbl_order_list_base b RIGHT JOIN tbl_order_lending_statistics l ON l.orderNo=b.orderNo WHERE b.cooperativeAgencyId>0 AND b.cooperativeAgencyId in (${agencyIds}) AND b.productCode!='04' AND b.state!='已关闭'
GROUP BY b.cooperativeAgencyId ORDER BY 1=1
,loanAmount ${loanAmountSort}
,orderCount ${orderCountSort}
,income ${incomeSort}

SELECT b.cooperativeAgencyId AS agencyId ,COUNT(DISTINCT f1.orderNo) AS payCount ,SUM(f1.payMentAmount) AS payMentAmount FROM tbl_order_list_base b RIGHT JOIN tbl_finance_receivable_for f1 ON f1.orderNo=b.orderNo WHERE b.cooperativeAgencyId>0 AND b.cooperativeAgencyId in (${agencyIds}) AND b.productCode!='04' AND b.state!='已关闭'
GROUP BY cooperativeAgencyId ORDER BY 1=1
,payCount ${payCountSort}
,payMentAmount ${payMentAmountSort}

SELECT COUNT(DISTINCT l.orderNo) AS orderNoCount , SUM(l.lendingAmount) AS lendingAmount, ff.fundId FROM tbl_risk_allocation_fund ff RIGHT JOIN tbl_order_lending_statistics l ON ff.orderNo = l.orderNo WHERE l.productCode!='04' AND ff.fundId in (${fundids})
GROUP BY ff.fundId ORDER BY 1=1
,lendingAmount ${fundLoanAmountSort}
,orderNoCount ${orderCountSort}

SELECT COUNT(DISTINCT f1.orderNo) AS payCount, SUM(f1.payMentAmount) AS payMentAmount,ff.fundId FROM tbl_risk_allocation_fund ff LEFT JOIN tbl_order_list_base b ON ff.orderNo = b.orderNo RIGHT JOIN tbl_finance_receivable_for f1 ON b.orderNo = f1.orderNo WHERE b.productCode!='04' AND b.state!='已关闭' AND ff.fundId in (${fundids})
AND b.productCode = #{productCode}
GROUP BY ff.fundId ORDER BY 1=1
,payMentAmount ${payMentAmountSort}
,payCount ${payCountSort}

mapper.update:
UPDATE tbl_order_report_personal_aim
SET effective = 2

	,updateUid = #{updateUid}

WHERE effective!=2

	AND deptId=#{deptId}


	AND `month` = #{month}


	AND `year` = #{year}


	AND cityCode = #{cityCode}

UPDATE tbl_order_report_income_file
SET effective = 2

	,deleteTime = #{deleteTime}


	,updateUid = #{updateUid}

WHERE 1=1

	AND deptId=#{deptId}


	and cityCode = #{cityCode}

mapper.select:
SELECT am.loanAim, am.incomeAim, am.orderAim, am.uid FROM tbl_order_report_personal_aim am WHERE am.effective = 1
AND am.`month` = ${month}
AND am.`year` = ${year}
AND am.deptId = #{deptId}

SELECT count(1) FROM ( SELECT u.id FROM tbl_user_base u LEFT JOIN tbl_user_role r ON r.id = u.roleId WHERE u.isEnable=0
AND u.agencyId = #{agencyId}
AND r.`name` = #{roleName}
AND u.cityCode = #{cityCode}
GROUP BY u.uid )t

SELECT u.`name` AS channelManagerName, u.uid AS channelManagerUid, u.cityCode
FROM tbl_user_base u LEFT JOIN tbl_user_role r ON r.id = u.roleId
WHERE u.isEnable=0
AND u.agencyId = #{agencyId}
AND r.`name` = #{roleName}
AND u.cityCode = #{cityCode}
